package romannumerals;import java.util.HashMap;public class RomanToNumeralsConverter {		private HashMap<Character, Integer> romanToArabicMap;		public RomanToNumeralsConverter() {				romanToArabicMap = new HashMap<Character, Integer>();		{			romanToArabicMap.put('I', 1);			romanToArabicMap.put('V', 5);			romanToArabicMap.put('X', 10);			romanToArabicMap.put('L', 50);			romanToArabicMap.put('C', 100);			romanToArabicMap.put('D', 500);			romanToArabicMap.put('M', 1000);		}			}			public int convert(String input) {						int result = 0;		char[] inputCharacters = input.toCharArray();				int prevValue = 0;		for (int i = inputCharacters.length - 1; i >= 0; i--) {			int currValue = romanToArabicMap.get(inputCharacters[i]);			if (currValue >= prevValue) {				result += currValue;			} else {				result -= currValue;			}			prevValue = currValue;		}			return result;	}		}